{{ripple}}/**
 * Maps annotation sample
 */

    var maps = new ej.maps.Maps({

        zoomSettings: {
            enable: false
        },
        annotations: [
            {
                content: '#maps-annotation',
                x: '0%', y: '70%'
            }, {
                content: '#compass-maps',
                x: '85%', y: '5%'
            }
        ],
        layers: [
            {
                shapeDataPath: 'name',
                shapePropertyPath: 'name',
                shapeData: new ej.maps.MapAjax('//npmci.syncfusion.com/development/demos/src/maps/map-data/africa-continent.json'),
                shapeSettings: {
                    fill: 'url(#grad1)'
                },
                markerSettings: [
                    {
                        visible: true,
                        template: '<h3 style="color:white">{{:name}}</h3>',
                        animationDuration: 1,
                        dataSource: [{
                                name: 'Africa', latitude: 13.97274101999902, longitude: 20.390625
                            }]
                    }
                ]
            }
        ]
    });
    maps.appendTo('#maps');
