    var maps = new ej.maps.Maps({

        titleSettings: {
            text: 'Location of Africa continent in the World map',
            textStyle: {
                size: '16px'
            }
        },
        zoomSettings: {
            enable: true
        },
        layers: [{
                layerType: 'OSM',
            },
            {
                type: 'SubLayer',
                animationDuration: 0,
                shapeData: new ej.maps.MapAjax('//npmci.syncfusion.com/development/demos/src/maps/map-data/africa.json'),
                shapeSettings: {
                    fill: '#5100a3',
                    opacity: 0.4
                }
            }]
    });
    maps.appendTo('#container');
