{{ripple}}/**
 * Marker zooming sample
 */

    var maps = new ej.maps.Maps({

        useGroupingSeparator: true,
        zoomSettings: {
            enable: true,
            mouseWheelZoom: false,
            pinchZooming: false
        },
        format: 'n',
        titleSettings: {
            text: 'Capitals of South American countries',
            textStyle: {
                size: '16px',
                fontFamily: 'Segeo UI'
            }
        },
        layers: [
            {
                shapeData: new ej.maps.MapAjax('//ej2.syncfusion.com/javascript/demos/src/maps/map-data/world-map.json'),
                dataSource: window.southAmericaCountryCapitals,
                shapeSettings: {
                    fill: '#C3E6ED',
                    border: {
                        color: 'black',
                        width: 0.3
                    }
                },
                markerSettings: [
                    {
                        dataSource: window.southAmericaCountryCapitals,
                        visible: true,
                        animationDuration: 0,
                        height: 20,
                        width: 20,
                        shape: 'Image',
                        imageUrl: '//ej2.syncfusion.com/javascript/demos/src/maps/images/ballon.png',
                        tooltipSettings: {
                            format:  '<b>Capital</b> : ${name}<br><b>Country</b> : ${Country}',
                            visible: true,
                            valuePath: 'name',
                            textStyle: {
                                fontFamily: 'Segeo UI'
                            }
                        }
                    },
                ]
            }
        ]
    });
    maps.appendTo('#container');
    var zoomIntiatally;
    var zoomIntiatallyCheckBox = new ej.buttons.CheckBox({
        change: zoomIntiatally, checked: false
    }, '#zoom');
    zoomIntiatallyCheckBox.change = zoomIntiatally = function (e) {
        maps.zoomSettings.shouldZoomInitially = e.checked;
    };
