var defaultRTE;

    var ajax = new ej.base.Ajax('//npmci.syncfusion.com/development/demos/src/rich-text-editor/ajax-content.html', 'GET', false);
    ajax.send().then(function(data) {
        defaultRTE = new ej.richtexteditor.RichTextEditor({ value: data });
        defaultRTE.appendTo('#defaultRTE');
    });
